#pragma config(Motor,  port2,           l_drive,       tmotorServoContinuousRotation, openLoop)
#pragma config(Motor,  port3,           r_drive,       tmotorServoContinuousRotation, openLoop)
//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//

void drive(bool reversed) {
	if(reversed) {
		motor[l_drive] = vexRT[Ch3];
		motor[r_drive] = vexRT[Ch2];
	}
	else {
		motor[l_drive] = -vexRT[Ch2];
		motor[r_drive] = -vexRT[Ch3];
	}
	if(vexRT[Btn8R] == 1) {
		while(vexRT[Btn8R] == 1) {}
		reversed = !reversed;
	}
}

task main()
{
	bool reversed = false;
	while(true) {
		drive(reversed);
	}
}
